mapper.update:

			SELECT #{stricExp} - stricExp
			FROM exp
			WHERE DATE(regDate) = DATE(#{regDate}) AND userId = #{userId} 		

  		UPDATE exp
  		SET stricExp = #{stricExp}
		WHERE DATE(regDate) = DATE(#{regDate}) AND userId = #{userId}

UPDATE exp
SET reviewExp = reviewExp + #{reviewExp}
WHERE DATE(regDate) = DATE(#{regDate}) AND userId = #{userId}
