mapper.update:
UPDATE USER
SET
	IMG = #{img},
	USERCOMMENT = #{userComment},
	NICKNAME = #{nickName}
WHERE USERID = #{userId}

UPDATE USER
SET
	PASSWORD = #{password}
WHERE USERID = #{userId}

mapper.select:
SELECT USERID
FROM USER
WHERE EMAIL = #{email}
AND USERNAME = #{userName}

mapper.update:
UPDATE user
SET totalExp = totalExp + #{diffStricExp}
WHERE userId = #{userId}

UPDATE user
SET totalExp = totalExp + 1, reviewCount = reviewCount + 1
WHERE userId = #{userId}

UPDATE user
SET totalExp = totalExp - 1, reviewCount = reviewCount - 1
WHERE userId = #{userId}
